import axios from 'axios'


const signup = async (fullName, email, password, state) => {
    const peticion = await axios.post('auth/signup',{
        email: `${email}`,
        password:`${password}`,
        fullName:`${fullName}`
    })
    .then((res)=> state(res.data))
    .catch((err)=>console.error(err));
}

const login = async (email, password,state) => {
    const peticion = await axios.post('auth/login',{
        email:`${email}`,
        password:`${password}`,
    })
    .then((res)=> state(res.data))
    .catch((err)=>console.error(err));
}

const getAll = async (state) => {
    const peticion = await axios.get('products/getAll')
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));
}

const deleteById = async (id, state) => {
    const peticion = await axios.delete(`products/deleteById/${id}`)
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));
}

const update = async (id, state) => {
    const peticion = await axios.put(`products/update/${id}`)
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));
}

const add = async (state) => {
    const peticion = await axios.post('products/add',
        [
            {
            name:'Iphone15',
            color:'Black',
            description:'Is an iphone 15',
            price:5.5
            }
        ]
    )
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));
}

export {
    signup,
    login
}
