import axios from 'axios'

axios.defaults.headers.common['Content-Type']='application/json';
axios.defaults.headers.common['Access-Control-Allow-Origin']='*';
axios.defaults.headers.common['Access-Control-Allow-Headers']='Origin, X-Requested-With, Content-Type, Accept';

const todosPersonajes = async (state) => {
    const peticion = await axios.get('https://rickandmortyapi.com/api/character');
    state(peticion.data.results)
}

const unicoPersonaje = async (id, state) => {
    const peticion = await axios.get(`https://rickandmortyapi.com/api/character/${id}`);
    state(peticion.data)
} 

const signup = async (state) => {
    const peticion = await axios.post('auth/signup',{
        email:'oscar.ramirez@gmail.com',
        password:'mypassword',
        fullName:'Oscar Ramirez Garcia'
    })
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));

}

export {
    todosPersonajes,
    unicoPersonaje,
    signup
}
