import axios from 'axios'


const signup = async (state) => {
    const peticion = await axios.post('auth/signup',{
        email:'oscar.ramirez@gmail.com',
        password:'mypassword',
        fullName:'Oscar Ramirez Garcia'
    })
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));
    console.log(peticion)
}

const login = async (state) => {
    const peticion = await axios.post('auth/login',{
        email:'oscar.ramirez@gmail.com',
        password:'mypassword',
    })
    .then((res)=> console.log(res.data))
    .catch((err)=>console.error(err));

}

export {
    signup,
    login
}
