import React, {useState} from 'react'
import Login from './Login';
import Registro from './Registro';
import Home from './Home';


export const Inicio = () => {
  const [showLogin, setShowLogin] = useState(true);
  const [showRegistro, setShowRegistro] = useState(false);
  const [textoBoton, setTextoBoton] = useState("Registrar nuevo usuario");

  const handleClick = () => {
    setShowLogin(!showLogin);
    setShowRegistro(!showRegistro);
    setTextoBoton(showLogin ? 'Volver':'Registrar nuevo usuario' );
  };

  return (
    <div>
      {showLogin && <LoginF />}
      {showRegistro && <RegistroF />}
      {showRegistro && <HomeF />}
      <button onClick={handleClick}>{textoBoton}</button>
    </div>
  );
}

function LoginF() {
  return <div><Login/></div>;
}

function RegistroF() {
  return <div><Registro/></div>;
}

export default Inicio
